mapper.select:
select material_info.*, GROUP_CONCAT(material_tag.title) AS tags, if(apaas_user.name is null, material_info.creator_name, apaas_user.name) as creator_name, max(material_info.update_time) as max_update_time from material_info left join apaas_user on apaas_user.id = material_info.creator_id left join material_tag_relation on material_info.id = material_tag_relation.material_id left join material_tag on material_tag.id = material_tag_relation.tag_id where 1 = 1
and material_info.status in
#{item}
and material_info.status in (1)
and material_info.type in
#{item}
and material_info.type in ('component', 'com_lib')
(material_info.creator_id = #{userId})
(material_info.scope_status = #{scope})
and material_info.id = material_tag_relation.material_id and material_tag_relation.tag_id in
#{tag}
and material_info.scene_id = #{scene}
and (material_info.title like '%${keyword}%' or material_info.description like '%${keyword}%' or material_info.namespace like '%${keyword}%')
GROUP BY material_info.id order by max_update_time DESC limit #{pageSize} offset #{offset};

select material_info.id as material_id, material_info.namespace, material_info.version, material_info.icon, material_info.docs, material_info.preview_img, material_info.title, material_info.description, material_info.type, material_info.creator_id, material_info.creator_name, material_info.create_time, material_info.updator_id, material_info.updator_name, material_info.update_time, material_pub_info.content, material_pub_info.commit_info from material_info, material_pub_info where material_info.id in
#{item}
and material_pub_info.version = material_info.version and material_pub_info.material_id = material_info.id

select
material_pub_info.*
from
    material_pub_info
where
material_pub_info.id in

    #{item}

order by FIELD(id,

    #{item}

)

select material_info.*,
material_pub_info.content,
material_pub_info.version as version, material_pub_info.id as material_pub_id, material_pub_info.material_id, CAST(SUBSTRING_INDEX(material_pub_info.version, '.', 1) AS UNSIGNED) major_version, CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(material_pub_info.version, '.', 2), '.', -1) AS UNSIGNED) minor_version, CAST(REPLACE(SUBSTRING_INDEX(SUBSTRING_INDEX(material_pub_info.version, '.', 3), '.', -1), '-beta', '') AS UNSIGNED) patch_version from material_info, material_pub_info where namespace = #{namespace}
and material_pub_info.status = #{status}
and material_pub_info.material_id = material_info.id and material_pub_info.version REGEXP '^([0-9]+)\\.([0-9]+)\\.([0-9]+)$' order by major_version desc, minor_version desc, patch_version desc limit 1;

mapper.update:
update material_pub_info
set

    content = #{nextInfo.content},


    commit_info = #{nextInfo.commitInfo},


    status = #{nextInfo.status},

update_time = #{nextInfo.updateTime},
updator_id = #{nextInfo.updatorId},
updator_name = #{nextInfo.updatorName}
where 1 = 1

    and id = #{query.id}


    and version = #{query.version}


    and material_id = #{query.materialId}

mapper.select:
select material_info.id, material_info.namespace, material_pub_info.version, material_info.icon, material_info.preview_img, material_info.title, material_info.description, material_info.type, material_info.creator_id, material_info.creator_name, material_info.create_time, material_info.updator_id, material_info.updator_name, material_info.update_time, material_pub_info.id as material_pub_id, material_pub_info.material_id, material_pub_info.content, material_pub_info.commit_info from material_info, material_pub_info where material_info.id = material_pub_info.material_id
(material_info.namespace = #{item.namespace} and material_pub_info.version = #{item.version})
and material_info.status = 1 and material_info.type = #{type};

select material_info.id, material_info.namespace, material_info.title, material_info.description, material_info.type, material_pub_info.version, material_pub_info.creator_id, if(apaas_user.name is null, apaas_user.email, apaas_user.name) as creator_name, material_pub_info.create_time, material_pub_info.id as material_pub_id, material_pub_info.material_id, material_pub_info.commit_info,
material_pub_info.content,
CAST(SUBSTRING_INDEX(material_pub_info.version, '.', 1) AS UNSIGNED) major_version, CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(material_pub_info.version, '.', 2), '.', -1) AS UNSIGNED) minor_version, CAST(REPLACE(SUBSTRING_INDEX(SUBSTRING_INDEX(material_pub_info.version, '.', 3), '.', -1), '-beta', '') AS UNSIGNED) patch_version, if(material_pub_info.version like '%-beta%', CAST(SUBSTRING_INDEX(SUBSTRING_INDEX(material_pub_info.version, '.', 4), '.', -1) AS UNSIGNED), 0) as beta_version from material_info, material_pub_info left join apaas_user on apaas_user.id = material_pub_info.creator_id where material_info.id = material_pub_info.material_id and material_info.status = 1
and material_info.namespace = #{namespace}
and material_info.id = #{materialId}
and material_pub_info.version NOT REGEXP '^([0-9]+)\\.([0-9]+)\\.([0-9]+)$'
and material_pub_info.version REGEXP '^([0-9]+)\\.([0-9]+)\\.([0-9]+)$'
order by major_version desc, minor_version desc, patch_version desc, beta_version desc;
